-- ----------------------------
-- Function structure for getbestlike
-- ----------------------------
DROP FUNCTION IF EXISTS "public"."getbestlike"(OUT "bestCount" numeric);
CREATE OR REPLACE FUNCTION "public"."getbestlike"(OUT "bestCount" numeric)
  RETURNS "pg_catalog"."numeric" AS $BODY$

BEGIN
		SELECT count("recomMessNum") into "bestCount"
		FROM "recommendMessageLike"
		WHERE date_part('MONTH',"recomMessLikeDateTime") = '3'
		GROUP BY "recomMessNum"
		ORDER BY "count" DESC
		LIMIT 1;  -- 2 
   
END; $BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

-- ----------------------------
-- Function structure for proc6
-- ----------------------------
DROP FUNCTION IF EXISTS "public"."proc6"(OUT "MessNum" numeric, OUT "procnt" numeric, OUT "empcnt" numeric);
CREATE OR REPLACE FUNCTION "public"."proc6"(OUT "MessNum" numeric, OUT "procnt" numeric, OUT "empcnt" numeric)
  RETURNS "pg_catalog"."record" AS $BODY$

BEGIN
		SELECT "recomMessNum", count("recomMessNum") into "MessNum", procnt
		FROM "recommendMessageLike"
		GROUP BY "recomMessNum"
		ORDER BY "count" DESC
		LIMIT 1;

    select count(*) into empcnt from "member";
END; $BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;

-- ----------------------------
-- View structure for articleListDataView
-- ----------------------------
DROP VIEW IF EXISTS "public"."articleListDataView";
CREATE VIEW "public"."articleListDataView" AS  SELECT "A"."artiNum",
    "A"."memID",
    "A"."artiDateTime",
    "A"."artiHead",
    "A"."artiCont",
    "A"."artiClass",
    "A"."likeCount",
    count(mess."artiNum") AS "messCount"
   FROM (( SELECT arti."artiNum",
            arti."memID",
            to_char(arti."artiDateTime", 'YYYY-MM-DD'::text) AS "artiDateTime",
            arti."artiHead",
            arti."artiCont",
            arti."artiClass",
            count("artiLike"."artiNum") AS "likeCount"
           FROM (article arti
             LEFT JOIN "articleLike" "artiLike" ON ((arti."artiNum" = "artiLike"."artiNum")))
          GROUP BY arti."memID", arti."artiDateTime", arti."artiHead", arti."artiCont", arti."artiClass", arti."artiNum") "A"
     LEFT JOIN "articleMessage" mess ON (("A"."artiNum" = mess."artiNum")))
  GROUP BY "A"."artiNum", "A"."memID", "A"."artiDateTime", "A"."artiHead", "A"."artiCont", "A"."artiClass", "A"."likeCount"
  ORDER BY "A"."artiNum" DESC;

-- ----------------------------
-- View structure for articleTagView
